import React from 'react'
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux'

function Tasks() {
  const [todoName, setTodo] = useState({ todo: "", listOfTodo: []});

  const submitTodo = () => {
    console.log("checking", todoName.todo)
    const newList = todoName.listOfTodo.push({
      todo: todoName.todo
      , isDone: false,
      listOfTodo: todoName.listOfTodo,
    })
    console.log("newlyIst", newList)
    // this.setState({
    // })
  }

  const onSubmit = e => {
    e.preventDefault();
    console.log(todoName)

  }

  return (
    <div>
      <form onSubmit={e => onSubmit(e)}>
        <input type='text' onChange={e => setTodo({ ...todoName, [e.target.name]: e.target.value })}/>
        <input type='submit' onClick={submitTodo}/>
      {todoName.listOfTodo.map(todo => (<div
        key={todo}>
        <div style={{ display: "flex" }}>
          <h2>{todo.name}</h2>

        </div>
      </div>))}
      </form>

    </div>
  )
}

export default Tasks;
