import React from 'react'
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux'

function Tasks() {
  const [todoName, setTodo] = useState({ todo: "", listOfTodo: []});

  const submitTodo = () => {
    console.log("checking", this.state.todoName)
    const newList = todoName.listOfTodo.push({
      name: todoName.todo
      , isDone: false
    })
    console.log("newlyIst", todoName.listOfTodo)
    this.setState({
      listOfTodo: todoName.listOfTodo,
    })
  }

  return (
    <div>
      <form>
        <input type='text' onChange={e => setTodo({ ...todoName, [e.target.name]: e.target.value })}/>
        <input type='submit' onClick={submitTodo}/>
      </form>
      {todoName.listOfTodos.map(todo => (<div
        key={todo}>
        <div style={{ display: "flex" }}>
          <h2>{todo.name}</h2>
          <input style={{ alignSelf: "center" }} checked={todo.isDone}
            onChange={() => this.handleCheckBox(todo.name)}
            type="checkbox" />
        </div>
      </div>))}

    </div>
  )
}

export default Tasks;
